using System.ComponentModel.DataAnnotations;namespace FootballManagerBLL.Dto.UserDto{    public class RegistrationModel    {        [Required(ErrorMessage = "First name is required.")]        [MaxLength(50, ErrorMessage = "First name must not exceed 50 characters.")]        public string FirstName { get; set; }        [Required(ErrorMessage = "Last name is required.")]        [MaxLength(50, ErrorMessage = "Last name must not exceed 50 characters.")]        public string LastName { get; set; }        [Required(ErrorMessage = "Email is required.")]        [EmailAddress(ErrorMessage = "Invalid email address.")]        [RegularExpression(".*@.*", ErrorMessage = "Email must contain the @ symbol.")]        public string Email { get; set; }        [Required(ErrorMessage = "Username is required.")]        [MaxLength(20, ErrorMessage = "Username must not exceed 20 characters.")]        public string Username { get; set; }        [Required(ErrorMessage = "Password is required.")]        [RegularExpression(@"^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{6,}$",            ErrorMessage = "Password must have a minimum length of 6 and contain at least 1 uppercase letter, 1 lowercase letter, 1 special character, and 1 digit.")]        public string Password { get; set; }        [Compare(nameof(Password), ErrorMessage = "The password confirmation does not match.")]        public string PasswordConfirm { get; set; }        public PlayerPosition? PlayerPosition { get; set; }        [MaxLength(50, ErrorMessage = "Role must not exceed 50 characters.")]        public string? Role { get; set; }    }}